import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [targetColor, setTargetColor] = useState('');
  const [message, setMessage] = useState('');
  const [userSelectedColor, setUserSelectedColor] = useState('');
  const [generatedColors, setGeneratedColors] = useState([]);

  useEffect(() => {
    generateColors();
  }, []);

  const generateColors = () => {
    const newColors = [];
    for (let i = 0; i < 3; i++) {
      newColors.push(pickRandomColor());
    }
    setGeneratedColors(newColors);
    setTargetColor(pickRandomColor());
  };

  const pickRandomColor = () => {
    const colors = ['pink', 'red', 'white', 'green', 'yellow', 'purple'];
    const randomIndex = Math.floor(Math.random() * colors.length);
    return colors[randomIndex];
  };

  const handleColorSelection = (color) => {
    setUserSelectedColor(color);
    checkColorMatch(color);
  };

  const checkColorMatch = (color) => {
    if (color === targetColor) {
      setMessage('Congratulations! You guessed it right!');
    } else {
      setMessage('Oops! Try again.');
    }
  };

  const handleNewGame = () => {
    setMessage('');
    setUserSelectedColor('');
    generateColors();
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Color Game</h1>
        <p>Click on the color that matches the RGB values of the target color:</p>
        <div className="color-container">
          {generatedColors.map((color, index) => (
            <div
              key={index}
              className={`color ${userSelectedColor === color ? 'selected' : ''}`}
              style={{ backgroundColor: color }}
              onClick={() => handleColorSelection(color)}
            ></div>
          ))}
        </div>
        <p>{message}</p>
        <button onClick={handleNewGame}>Start Roll</button>
      </header>
    </div>
  );
}

export default App;
